package com.yunzhanghu.redpacketui.alipay;import android.text.TextUtils;import java.util.Map;class PayResult {    private String resultStatus;    private String result;    private String memo;    PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}